          url: '<?php echo base_url();?>/Welcome/tienda',
          type: 'POST',
          dataType: 'php',
          data: {busqueda: busqueda},
        })
        .done(function(respuesta){
          $("#actualizar").php(respuesta);
        })
        .fail(function(r){
          console.log('error');
        })
      }

      $(document).on('keyup', '#buscar', function(){
        var valor = $(this).val();
        if (valor != "") {
          buscar_tienda(valor);
        }else{
          buscar_tienda();
        }
      });
</script>
    